'use strict';

module.exports = angular.module('npAdmin')
.config(['$httpProvider', '$stateProvider', '$urlRouterProvider',
    function($httpProvider, $stateProvider, $urlRouterProvider) {
        $stateProvider
            .state('common', {
                templateUrl: '/templates/common/tpl.common.html',
                controller: 'HeaderCtrl'
            })
            .state('common_subtpl', {
                templateUrl: '/templates/common/tpl.common_subtpl.html',
                parent: 'common',
                controller: 'TopPanelCtrl'
            })
            .state('dashboard', {
                url: '/dashboard?questions_type',
                parent: 'common_subtpl',
                templateUrl: '/templates/dashboard/dashboard.html',
                controller: 'AllQuestionsCtrl'
            })
            .state('ask_question', {
                url: '/ask-question',
                parent: 'common_subtpl',
                templateUrl: '/templates/ask_question/ask_question.html',
                controller: 'AskQuestionCtrl'
            })
            .state('separate_question', {
                url: '/separate-question?id',
                parent: 'common_subtpl',
                templateUrl: '/templates/separate_question/separate_question.html',
                controller: 'SeparateQuestionCtrl'
            })
            .state('login', {
                url: '/login',
                parent: 'common',
                templateUrl: '/templates/login/login.html',
                controller: 'LoginCtrl'
            })
        $urlRouterProvider.otherwise('/dashboard?questions_type=all');
    }
]);