(function() {
  'use strict';
 
  describe('Login functionality', function() {
    beforeEach(function() {
      browser.get('http://localhost:8080');
    });

    it('should show login page if user is logged out', function() {
      expect(element(by.tagName('nav')).isDisplayed()).toBeTruthy();
      element.all(by.css('nav li')).
        get(3).
        element(by.linkText('Ask a question')).
        click();
      expect(element(by.css('input#username')).isDisplayed()).toBeTruthy();
    });
  });
}());